mapper.insert:
INSERT INTO oa_customer_relation(
	id,
	oa_customer_id,
	user_id
) VALUES (
	#{id},
	#{oaCustomer.id},
	#{user.id}
)

mapper.update:
UPDATE oa_customer_relation SET 	
	oa_customer_id = #{oaCustomer.id},
	user_id = #{user.id}
WHERE id = #{id}

mapper.delete:
DELETE FROM oa_customer_relation


		WHERE id = #{id}


		WHERE oa_customer_id = #{oaCustomer.id}
