mapper.select:
SELECT 

FROM oa_message a

WHERE a.id = #{id}

SELECT
FROM oa_message a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.title LIKE '%'||#{title}||'%' '%'+#{title}+'%' concat('%',#{title},'%')
AND a.content LIKE '%'||#{content}||'%' '%'+#{content}+'%' concat('%',#{content},'%')
AND a.create_date = #{createDate}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM oa_message a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 
	count(1)
FROM oa_message a

	JOIN oa_message_record r ON r.oa_message_id = a.id AND r.user_id = #{currentUser.id}

			AND r.read_flag = #{readFlag}


WHERE a.del_flag = #{DEL_FLAG_NORMAL}

mapper.update:
UPDATE oa_message SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
