mapper.update:
UPDATE oa_message_record SET 	
	oa_message_id = #{oaMessage.id},
	user_id = #{user.id},
	read_flag = #{readFlag},
	read_date = #{readDate}
WHERE id = #{id}

mapper.delete:
DELETE FROM oa_message_record


		WHERE id = #{id}


		WHERE oa_message_id = #{oaMessage.id}
